var app=new Vue({el:"#app",data:{backgroundColor:"white",message:"Vue To-Do",todos:[]},template:'<div class=\'container\'>\n    <h1> {{ message }} </h1>\n    <div class="form-container">\n      <input id=\'input\' placeholder="Add New Item.."  @keydown.enter="addItem">\n      <button @click="addItem">Add Item</button>\n      <button @click="removeList">Clear List</button>\n    </div>\n    <ol id=\'itemList\'>\n      <li v-for=\'(todo, index) in todos\'\n        :key="index">\n        <input id="cb" @click="removeItem(index)" type="checkbox">\n        <span :style="textEdit" contentEditable="true">\n          {{todo.text}}\n        </span>\n      </li>\n    </ol>\n  </div>',methods:{addItem(){let t=document.getElementById("input");t.value.length>0&&app.todos.push({text:t.value}),t.value="",saveList()},removeItem(t){app.todos.splice(t,1),document.getElementById("cb").checked=!1,saveList()},removeList(){let t=document.querySelector("#itemList");for(;t.childNodes;)t.removeChild(t.childNodes[0]);save()},mounted(){localStorage.getItem("todos")&&(this.todos.text=JSON.parse(localStorage.getItem("todos")))},saveList(){let t=JSON.stringify(this.todos);localStorage.setItem("todos",t)}},computed:{textEdit(){return{backgroundColor:this.backgroundColor}}},watch:{todos:{handler(){localStorage.setItem("todos",JSON.stringify(this.todos))},deep:!0}}});